script:
frappe.ui.form.on('Item', {
    item_coding_table_link: function(frm) {
        if (frm.doc.item_coding_table_link) {
            frappe.call({
                method: "erpnext_engineering.engineering.doctype.item_coding_table.item_coding_table.get_full_coding_description",
                args: {
                    coding_code: frm.doc.item_coding_table_link
                },
                callback: function(r) {
                    frm.set_value('item_coding_table_description', r.message || "");
                }
            });
        } else {
            frm.set_value('item_coding_table_description', "");
        }
    }
});
